package com.syntax.class01;

public class HW3Sept10 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("First way to do it:");
		
		int t=20;
		
		while(t>=1) {
			if(t%2==0) {
				
				System.out.print(t+ " ");
			}
			t--;
		}
		
		
		
		System.out.println("--------------------------------");
		System.out.println("--------------------------------");
		
		System.out.println("Second way to do it");
		
		int d=20;
		
		while(d>=1) {
			
			System.out.print(d+ " ");
			d-=2;
			
			
		}
	}

}
