package com.syntax.class01;

public class VariableValues {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte lakers=7;
		short heat = 5000; 
		int pistons = 400000;
		long jazz = 20000000;
		
		
		float clippers = 2.79F;
		double wizards = 88.0;
		
		
		char nuggets= 'D';
		char warriors= '$';
		
		
		boolean thunder= true;
		boolean celtics= false;
		
		System.out.println(lakers);
		System.out.println(heat);
		System.out.println(pistons);
		System.out.println(jazz);
		System.out.println(clippers);
		System.out.println(wizards);
		System.out.println(nuggets);
		System.out.println(warriors);
		System.out.println(thunder);
		System.out.println(celtics);
		
		
		
		
	}

}
